export class CustomPathValidator {
  // Validates path
  static pathValidator(path) {
    if (path.pristine) {
      return null;
    }

    const PATH_REGEXP = /^\/[a-z]*[A-Z]*[0-9]*$/;
    path.markAsTouched();

    if (PATH_REGEXP.test(path.value)) {
      return null;
    }

    return {
      invalidPath: true
    };
  }

}
